package br.com.builder;public class FuncionarioMontador implements Montador {	private Pedido pedido = new Pedido();	public void adicionarSanduiche(String sanduiche) {		pedido.adicionarDentroDaCaixa(sanduiche);	}	public void adicionarBatata(String batata) {		pedido.adicionarDentroDaCaixa(batata);	}	public void adicionarBrinquedo(String brinquedo) {		pedido.adicionarDentroDaCaixa(brinquedo);	}	public void adicionarRefrigerante(String refrigerante) {		pedido.adicionarForaDaCaixa(refrigerante);	}	public Pedido getPedido() {		return pedido;	}}